import React, { useState} from 'react';

export default function SignIn() {
    let [email, setEmail] = useState('');
    let [password, setPassword] = useState('');
    const [errors, setErrors] = useState({});


    const handleOnClick = async (e) => {
        e.preventDefault();
        email = document.getElementById("email").value;
        password = document.getElementById("password").value
        // if (validateForm()) {
        //     const emailRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
        //     let signInRequest = {
        //         email: email,
        //         password: password
        //     }
        //     //console.log(signInRequest)
        //     const signInResponse = await loginAction(signInRequest)

        //     console.log(signInResponse);
        // }
    };

    const validateForm = () => {
        const newErrors = {};
        const emailRegex =  /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;
        const hasUpperCase = /[A-Z]/.test(password);
        const hasLowerCase = /[a-z]/.test(password);
        const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);
        const hasNumber = /[0-9]/.test(password);


        if (email.length === 0) {
            newErrors.email = 'Email is required';
        } else if (!emailRegex.test(email)) {
            newErrors.email = 'Email is not valid';
            console.log(emailRegex.test(email))
        }
        if (password.length === 0) {
            newErrors.password = 'Password is required';
        } else if (password.length < 8 || !hasLowerCase || !hasUpperCase || !hasSpecialChar || !hasNumber ) {
            newErrors.password = 'See the password requirements by clicking on info icon';
        }
        //console.log("newErrors", newErrors);

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;

     };
    return (
        <div
            className=" flex h-screen w-full items-center justify-center mt-1 ml-10">
            <div className=" w-full max-w-xl h-[500px] flex flex-col items-center p-12 bg-white rounded-lg shadow-md"
                 style={{top: "-20px", left: "-20px"}}>

                <div className="flex justify-center -mt-5 -mb-12">
                    <img src={logo} alt="Logo" className="w-40"/>
                </div>
                <div
                    className="-mt-12 flex h-full w-full items-center justify-center px-2 md:mx-0 md:px-0 lg:mb-10 lg:items-center lg:justify-start">
                    {/* Sign in section */}
                    <div className="mt-[12vh] w-full max-w-full flex-col items-center md:pl-4 lg:pl-0 xl:max-w-[420px]">
                        <h4 className="mb-2.5 text-2xl font-bold text-navy-700 dark:text-white">
                            Sign In 
                        </h4>
                        <p className="mb-8 ml-1 mt-[2vh] text-base text-black">
                            Enter your email and password to sign in!
                        </p>

                        {/* Email */}
                        <InputField
                            variant="auth"
                            extra="mb-3"
                            label="Email*"
                            placeholder="mail@simmmple.com"
                            //onChange={handleEmailChange}
                            id="email"
                            type="text"
                            onChange={(e) => setEmail(e.target.value)}

                        />
                        {errors.email && <span className="text-red-500">{errors.email}</span>}
                        {/* Password */}
                        <PasswordFieldEyeButton
                            variant="auth"
                            extra="mb-3"
                            label="Password*"
                            placeholder="Min. 8 characters"
                            //onChange={handleInputChange}
                            id="password"
                            type="password"
                            onChange={(e) => setPassword(e.target.value)}
                        />{errors.password && (
                        <span className="text-red-500">{errors.password}</span>
                    )}
                        {/* Checkbox */}
                        <div className="mb-4 flex items-center justify-between px-2">
                            <Link
                                to="/senze-ecms/auth/forget-password"
                                className="text-sm font-medium text-brand-500 hover:text-brand-600 dark:text-white">
                                Forgot Password?
                            </Link>

                        </div>
                        <button
                            onClick={handleOnClick}
                            className="linear mt-2 w-full rounded-xl bg-brand-500 py-[12px] text-base font-medium text-white transition duration-200 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:text-white dark:hover:bg-brand-300 dark:active:bg-brand-200">
                            Sign In
                        </button>

                    </div>

                </div>
            </div>
        </div>
    );
}
